import React, { useState } from 'react';
import './Login.css';
import login from './login.png';
import writo from './writo.png';

const Login = () => {
  const [loading, setLoading] = useState(false);

  const handleLogin = (e) => {
    e.preventDefault();
    setLoading(true);

    // Simulate login process (You can replace this with your actual login logic)
    setTimeout(() => {
      setLoading(false);
      // Add login success logic here (e.g., redirect)
    }, 2000); // Simulate 2 seconds loading time
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <div className="left-section">
          <img src={writo} alt="Writo" className="writo-img" />
          <h1>Writo Education</h1>
          <form className="login-form" onSubmit={handleLogin}>
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input type="email" id="email" placeholder="Enter your email" required />
            </div>
            <div className="form-group">
              <label htmlFor="password">Password</label>
              <input type="password" id="password" placeholder="Enter your password" required />
            </div>
            <button type="submit" className="login-btn">
              {loading ? (
                <div className="spinner"></div>  // Loading spinner
              ) : (
                "Login"  // Default button text
              )}
            </button>
          </form>
          <p className="signup-text">
            Don't have an account? <a href="/signup" className="signup-link">Sign Up</a>
          </p>
        </div>
        <div className="right-section">
          <img src={login} alt="Login illustration" className="login-image" />
        </div>
      </div>
    </div>
  );
};

export default Login;
