import React, { useState, useEffect } from "react";
import './Home.css'; 
import logo from './logo.png';

const Home = () => {
    const [currentText, setCurrentText] = useState('');
    const texts = ["a win", "a thrill", "a breeze"]; // Array of messages
    const [textIndex, setTextIndex] = useState(0);
    const [typing, setTyping] = useState(true);
    const [pauseAfterTyping, setPauseAfterTyping] = useState(false);

    // Define colors for each text
    const colors = ["#f27272"]; // Red, yellow, Blue

    useEffect(() => {
        let timeout;

        if (typing && !pauseAfterTyping) {
            timeout = setTimeout(() => {
                setCurrentText(texts[textIndex].slice(0, currentText.length + 1));
                if (currentText.length === texts[textIndex].length) {
                    setTyping(false);
                    setPauseAfterTyping(true);
                    setTimeout(() => setPauseAfterTyping(false), 1000);
                }
            }, 150); // Slow down typing speed
        } else if (!typing && !pauseAfterTyping) {
            timeout = setTimeout(() => {
                setCurrentText(texts[textIndex].slice(0, currentText.length - 1));
                if (currentText.length === 0) {
                    setTextIndex((prevIndex) => (prevIndex + 1) % texts.length);
                    setTyping(true);
                }
            }, 100); // Slow down erasing speed
        }

        return () => clearTimeout(timeout);
    }, [currentText, typing, pauseAfterTyping, textIndex, texts]);

    return (
        <div className="home-container">
            <nav className="navbar">
                <div className="logo">
                    <img src={logo} alt="Logo" className="logo-img" />
                    <span className="company-name">WRITO EDUCATION</span>
                </div>
                <div className="nav-buttons">
                    <button className="nav-btn">Login</button>
                    <button className="nav-btn">Signup</button>
                </div>
            </nav>

            <div className="main-content">
                <h1 className="main-heading">
                    Exams are <span className="dynamic-text" style={{ color: colors[textIndex] }}>{currentText}<span className="typing-caret"></span></span>
                    
                </h1>
                <h1 className="main-heading">with<span> Writo's</span> expertise!</h1>
                <p className="slogan">"Empowering minds for a brighter future."</p>
                <button className="explore-btn">Explore</button>
            </div>
        </div>
    );
};

export default Home;
