import React, { useState, useEffect } from "react";
import './Home.css';

const Home = () => {
    const [currentText, setCurrentText] = useState('');
    const texts = ["a win", "a thrill", "a breeze"]; // Array of messages
    const [textIndex, setTextIndex] = useState(0);
    const [typing, setTyping] = useState(true);
    const [pauseAfterTyping, setPauseAfterTyping] = useState(false);

    // Define colors for each text
    const colors = ["#FF6347", "#32CD32", "#1E90FF"]; // Red, Green, Blue

    useEffect(() => {
        let timeout;

        if (typing && !pauseAfterTyping) {
            timeout = setTimeout(() => {
                setCurrentText(texts[textIndex].slice(0, currentText.length + 1));
                if (currentText.length === texts[textIndex].length) {
                    setTyping(false);
                    setPauseAfterTyping(true);
                    setTimeout(() => setPauseAfterTyping(false), 1000);
                }
            }, 100);
        } else if (!typing && !pauseAfterTyping) {
            timeout = setTimeout(() => {
                setCurrentText(texts[textIndex].slice(0, currentText.length - 1));
                if (currentText.length === 0) {
                    setTextIndex((prevIndex) => (prevIndex + 1) % texts.length);
                    setTyping(true);
                }
            }, 100);
        }

        return () => clearTimeout(timeout);
    }, [currentText, typing, pauseAfterTyping, textIndex, texts]);

    return (
        <div className="typing-container">
            <h1 className="typing-animation">
                Exams are <span className="dynamic-text" style={{ color: colors[textIndex] }}>{currentText}</span>
            </h1>
        </div>
    );
};

export default Home;
