import React, { useState, useEffect } from "react";
import './Home.css';

const Home = () => {
    const [currentText, setCurrentText] = useState('');
    const texts = ["Exams are a win", "Exams are a thrill", "Exams are a breeze"]; // Array of messages
    const [textIndex, setTextIndex] = useState(0);
    const [typing, setTyping] = useState(true);
    const [pauseAfterTyping, setPauseAfterTyping] = useState(false);

    // Function to handle typing effect
    useEffect(() => {
        let timeout;

        if (typing && !pauseAfterTyping) {
            // Simulate typing by gradually revealing the text
            timeout = setTimeout(() => {
                setCurrentText(texts[textIndex].slice(0, currentText.length + 1));
                if (currentText.length === texts[textIndex].length) {
                    setTyping(false);
                    setPauseAfterTyping(true);
                    // Longer pause after typing completes
                    setTimeout(() => setPauseAfterTyping(false), 2000); // Adjust this delay for longer pause
                }
            }, 100); // Adjust typing speed here (lower is faster)
        } else if (!typing && !pauseAfterTyping) {
            // Simulate deleting text
            timeout = setTimeout(() => {
                setCurrentText(texts[textIndex].slice(0, currentText.length - 1));
                if (currentText.length === 0) {
                    // Move to the next text and start typing again
                    setTextIndex((prevIndex) => (prevIndex + 1) % texts.length);
                    setTyping(true);
                }
            }, 100); // Adjust deleting speed here
        }

        return () => clearTimeout(timeout); // Clean up timeout on component unmount
    }, [currentText, typing, pauseAfterTyping, textIndex, texts]);

    return (
        <div className="typing-container">
            <h1 className="typing-animation">{currentText}</h1>
        </div>
    );
};

export default Home;
